      &amp;lt;/button&amp;gt;
      &amp;lt;button class=&amp;quot;h-8 w-8 rounded-full flex items-center justify-center border border-[var(--color-border)] bg-[var(--color-primary)] text-white&amp;quot;&amp;gt;1&amp;lt;/button&amp;gt;
