&amp;lt;script&amp;gt;
  document.addEventListener(&amp;#039;DOMContentLoaded&amp;#039;, () =&amp;gt; {
    // Simple accordion functionality for the examples
    const accordionButtons = document.querySelectorAll(&amp;#039;.eds-accordion button&amp;#039;);

    accordionButtons.forEach(button =&amp;gt; {
      button.addEventListener(&amp;#039;click&amp;#039;, () =&amp;gt; {
        const isExpanded = button.getAttribute(&amp;#039;aria-expanded&amp;#039;) === &amp;#039;true&amp;#039;;
        const controlsId = button.getAttribute(&amp;#039;aria-controls&amp;#039;);
        const content = controlsId ? document.getElementById(controlsId) : button.nextElementSibling;
        const icon = button.querySelector(&amp;#039;svg&amp;#039;);

        if (isExpanded) {
          button.setAttribute(&amp;#039;aria-expanded&amp;#039;, &amp;#039;false&amp;#039;);
          if (content) content.classList.add(&amp;#039;hidden&amp;#039;);
          if (icon) icon.classList.remove(&amp;#039;rotate-180&amp;#039;);
        } else {
          button.setAttribute(&amp;#039;aria-expanded&amp;#039;, &amp;#039;true&amp;#039;);
          if (content) content.classList.remove(&amp;#039;hidden&amp;#039;);
          if (icon) icon.classList.add(&amp;#039;rotate-180&amp;#039;);
        }
      });
    });
  });
&amp;lt;/script&amp;gt;

&amp;lt;h2 id=&amp;quot;welcome-to-the-enterprise-design-system&amp;quot; tabindex=&amp;quot;-1&amp;quot;&amp;gt;Welcome to the Enterprise Design System &amp;lt;a class=&amp;quot;anchor&amp;quot; href=&amp;quot;#welcome-to-the-enterprise-design-system&amp;quot; aria-hidden=&amp;quot;true&amp;quot;&amp;gt;#&amp;lt;/a&amp;gt;&amp;lt;/h2&amp;gt;
&amp;lt;p&amp;gt;The Enterprise Design System (EDS) provides a comprehensive set of design guidelines, components, and resources for building consistent, accessible, and user-friendly enterprise applications. Our goal is to streamline the design and development process, reduce redundancy, and create a cohesive user experience across all enterprise products.&amp;lt;/p&amp;gt;
&amp;lt;h3 id=&amp;quot;recent-updates&amp;quot; tabindex=&amp;quot;-1&amp;quot;&amp;gt;Recent Updates &amp;lt;a class=&amp;quot;anchor&amp;quot; href=&amp;quot;#recent-updates&amp;quot; aria-hidden=&amp;quot;true&amp;quot;&amp;gt;#&amp;lt;/a&amp;gt;&amp;lt;/h3&amp;gt;
&amp;lt;ul&amp;gt;
&amp;lt;li&amp;gt;&amp;lt;strong&amp;gt;Version 2.0 Released&amp;lt;/strong&amp;gt;: Our latest version includes improved accessibility, new components, and enhanced documentation. &amp;lt;a href=&amp;quot;/updates/2025-02-15-version-2-release/&amp;quot;&amp;gt;Learn more&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;
&amp;lt;li&amp;gt;&amp;lt;strong&amp;gt;Dark Mode Support&amp;lt;/strong&amp;gt;: All components now support dark mode out of the box, with seamless theme switching.&amp;lt;/li&amp;gt;
&amp;lt;li&amp;gt;&amp;lt;strong&amp;gt;Expanded Component Library&amp;lt;/strong&amp;gt;: We&amp;#039;ve added 15 new components based on user feedback and real-world use cases.&amp;lt;/li&amp;gt;
&amp;lt;/ul&amp;gt;
&amp;lt;h3 id=&amp;quot;about-the-design-system&amp;quot; tabindex=&amp;quot;-1&amp;quot;&amp;gt;About the Design System &amp;lt;a class=&amp;quot;anchor&amp;quot; href=&amp;quot;#about-the-design-system&amp;quot; aria-hidden=&amp;quot;true&amp;quot;&amp;gt;#&amp;lt;/a&amp;gt;&amp;lt;/h3&amp;gt;
&amp;lt;p&amp;gt;The Enterprise Design System is built on the following principles:&amp;lt;/p&amp;gt;
&amp;lt;ol&amp;gt;
&amp;lt;li&amp;gt;&amp;lt;strong&amp;gt;Consistency&amp;lt;/strong&amp;gt;: Creating a unified experience across all enterprise applications.&amp;lt;/li&amp;gt;
&amp;lt;li&amp;gt;&amp;lt;strong&amp;gt;Accessibility&amp;lt;/strong&amp;gt;: Ensuring all components meet WCAG 2.1 AA standards.&amp;lt;/li&amp;gt;
&amp;lt;li&amp;gt;&amp;lt;strong&amp;gt;Flexibility&amp;lt;/strong&amp;gt;: Providing components that can be customized to meet various needs.&amp;lt;/li&amp;gt;
&amp;lt;li&amp;gt;&amp;lt;strong&amp;gt;Performance&amp;lt;/strong&amp;gt;: Optimizing components for fast load times and smooth interactions.&amp;lt;/li&amp;gt;
&amp;lt;li&amp;gt;&amp;lt;strong&amp;gt;Maintainability&amp;lt;/strong&amp;gt;: Creating a system that is easy to update and extend.&amp;lt;/li&amp;gt;
&amp;lt;/ol&amp;gt;
&amp;lt;p&amp;gt;Visit our &amp;lt;a href=&amp;quot;/getting-started/&amp;quot;&amp;gt;getting started guide&amp;lt;/a&amp;gt; to learn how to integrate the design system into your projects.&amp;lt;/p&amp;gt;
